import {useState,useEffect} from 'react'
import SkillContainer from "./SkillContainer"
const Skills = () => {

    const [skillContainers,setOpen] = useState(
        [{
            id:1,
            title:"All",
            open:true,
            content:"{Java\tPython\tSwift\tJavascript\tHTML\tCSS\tReact\tBootstrap5\tGithub\tAndroid Studio\tData Mapping\tAdobe Prepare tools}"
        },
        {
            id:2,
            title:"Languages",
            open:false,
            content:"Java\tPython\tSwift\tJavascript\tHTML\tCSS"
        },
        {
            id:3,
            title:"Software",
            open:false,
            content:"React\tBootstrap5\tGithub\tAndroid Studio"
        },
        {
            id:4,
            title:"Other",
            open:false,
            content:"Data Mapping\tAdobe Prepare tools"
        }]
    )

        const open = (id) => {
            setOpen(skillContainers.map((skillContainer) => (
                skillContainer.id == id ? {...skillContainer,open:!skillContainer.open}:skillContainer)))
        }

    return (
        <section id="skills">
            <h1 className="sectionTitle">Skills<span className="punctuation">.</span></h1>
            <div>
                {
                    skillContainers.map((skillContainer) => (
                        <SkillContainer 
                        id={skillContainer.id}
                        title={skillContainer.title}
                        open={skillContainer.open}
                        content={skillContainer.content}
                        toggleOpen={open}
                        />))
                }
            </div>
        </section>
    )
}

export default Skills
